package week1.day2;

public class LearningBitwiseOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 5;
        int b = 7;
 
        // bitwise AND operator
      
        System.out.println("a&b = " + (a & b));
        
        // bitwise OR operator
        System.out.println("a|b = " + (a | b));
	}

}
