package week1.day2;

public class LearningIncrementDecrement {

	public static void main(String[] args) {
		
		int numberOne=1;
		
		System.out.println("numberOne++ " + (numberOne++));
		System.out.println("++numberOne " + (++numberOne));
		System.out.println("numberOne-- " + (numberOne--));
		System.out.println("--numberOne " + (--numberOne));

		
		// TODO Auto-generated method stub

	}

}
