package week1.day2;

public class LearningRelationalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 20, b = 10;
        
        
        System.out.println("Is a == b :" + (a == b));
        System.out.println("Is a < b :" + (a < b));
        System.out.println("Is a <= b :" + (a <= b));
        System.out.println("Is a > b :" + (a > b));
        System.out.println("Is a >= b :" + (a >= b));
        System.out.println("Is a != b :" + (a != b));

	}

}
