package week1.day2;

public class LearningTernaryOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int a = 20, b = 10, result;
		 
	        //result holds max of three
	        //numbers
	        result = ((a > b)? a:b);
	        
	        System.out.println("Max of three numbers = "+result);
	}

}
